import React, {useState} from 'react';
import axios from 'axios';
export default function Upload(){
  const [file,setFile]=useState(null); const [msg,setMsg]=useState('');
  const token = localStorage.getItem('token');
  const submit = async (e)=>{
    e.preventDefault();
    if(!file) return setMsg('Select file');
    const fd = new FormData();
    fd.append('file', file);
    try{
      const res = await axios.post((process.env.REACT_APP_API_URL||'http://localhost:5000')+'/api/upload', fd, {headers:{Authorization:'Bearer '+token, 'Content-Type':'multipart/form-data'}});
      setMsg(res.data.message);
      window.location.reload();
    }catch(err){ setMsg(err.response?.data?.message || 'Upload failed') }
  }
  return (<div className="card">
    <h3>Upload CSV</h3>
    <form onSubmit={submit}>
      <input type="file" accept=".csv,.xlsx,.axls" onChange={e=>setFile(e.target.files[0])}/>
      <button type="submit">Upload & Distribute</button>
    </form>
    {msg && <p>{msg}</p>}
    <p style={{fontSize:12}}>CSV must have columns: FirstName,Phone,Notes</p>
  </div>);
}
