import React, {useEffect, useState} from 'react';
import axios from 'axios';
import Agents from './Agents';
import Upload from './Upload';

const API = process.env.REACT_APP_API_URL||'http://localhost:5000';
export default function Dashboard(){
  const [agents,setAgents]=useState([]);
  const [assignments,setAssignments]=useState([]);
  const token = localStorage.getItem('token');
  useEffect(()=>{
    const fetchData = async ()=>{
      try{
        const a = await axios.get(API+'/api/agents', {headers:{Authorization:'Bearer '+token}});
        setAgents(a.data.agents);
        const as = await axios.get(API+'/api/upload/assignments', {headers:{Authorization:'Bearer '+token}});
        setAssignments(as.data.assignments);
      }catch(err){ console.error(err) }
    }
    fetchData();
  },[]);
  return (<div className="container">
    <h1>Dashboard</h1>
    <div className="row">
      <div>
        <Agents agents={agents} />
        <Upload />
      </div>
      <div className="card">
        <h3>Assignments</h3>
        {assignments.map(a=>(
          <div key={a._id} className="assign">
            <strong>{a.agent.name} ({a.agent.email})</strong>
            <p>Items: {a.items.length}</p>
            <ul>
              {a.items.map(it=> <li key={it._id}>{it.firstName} — {it.phone} — {it.notes}</li>)}
            </ul>
          </div>
        ))}
      </div>
    </div>
  </div>);
}
